import 'package:flutter/material.dart';
import 'package:firebase_core/firebase_core.dart';
import 'package:provider/provider.dart';
import 'package:capstoneproject_mobileapp/pages/login_page.dart';
import 'package:capstoneproject_mobileapp/pages/home_page.dart';
import 'package:capstoneproject_mobileapp/pages/user_model.dart'; // Ensure this exists

// Generated by flutterfire configure

void main() async {
  WidgetsFlutterBinding.ensureInitialized();
  await Firebase.initializeApp();
  runApp(
    ChangeNotifierProvider(
      create: (context) => UserProvider(),
      child: const MyApp(),
    ),
  );
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Capstone Project Mobile App',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: const LoginPage(), // or HomePage if user is already logged in
    );
  }
}
}

